mapper.select:
   
   SELECT   *
    FROM   (SELECT    /*+INDEX_DESC(tbl_board pk_board)*/
               rownum rn,
              RE_IDX, TOTALPRICE, DEPOSIT, ADAULT_NUMBER, CHILD_NUMBER, BABY_NUMBER, RE_DATE, KOREAN_NAME, ENGLISH_NAME,
				BIRTH, PHONE, EMAIL, REQUESTED, STATUS, ID, SEX, PRODUCT_CODE, PRODUCT_NAME
           FROM   TA_RESERVATION RE, TA_PRODUCT PR
         WHERE   rownum <= #{page} * #{perPageNum}
         AND      RE_IDX > 0  AND RE.PRODUCT_CODE = PR.PRODUCT_CODE
         )
   WHERE    rn > (#{page} -1) * #{perPageNum}

SELECT * FROM (SELECT /*+INDEX_DESC(tbl_board pk_board)*/ rownum rn, RE.RE_IDX, RE.TOTALPRICE, RE.DEPOSIT, RE.ADAULT_NUMBER, RE.CHILD_NUMBER, RE.BABY_NUMBER, RE.RE_DATE, KOREAN_NAME, ENGLISH_NAME, BIRTH, PHONE, EMAIL, REQUESTED, STATUS, ID, SEX, RE.PRODUCT_CODE, PR.PRODUCT_NAME FROM TA_RESERVATION RE, TA_PRODUCT PR
AND rownum <= #{page} * #{perPageNum} AND STATUS IN('03', '04') AND RE_IDX > 0 AND RE.PRODUCT_CODE = PR.PRODUCT_CODE )
WHERE rn > (#{page} -1) * #{perPageNum}
