SELECT * FROM (SELECT /*+INDEX_DESC(tbl_board pk_board)*/ rownum rn, OD.ORDER_CODE, OD.ORDER_MEANS, OD.ORDER_PRICE, OD.ORDER_DATE, OD.STATUS, RE.ID, PR.PRODUCT_CODE, PR.PRODUCT_NAME FROM TA_RESERVATION RE, TA_PRODUCT PR, TA_ORDER OD
AND rownum <= #{page} * #{perPageNum} AND OD.ORDER_CODE > 0 AND RE.PRODUCT_CODE = PR.PRODUCT_CODE AND RE.RE_IDX = OD.RE_IDX AND OD.STATUS IN('02', '03') )
WHERE rn > (#{page} -1) * #{perPageNum}
